console.log("search"),(()=>{let e=document.getElementById("top_news");const n=new XMLHttpRequest;n.open("GET","https://newsapi.org/v2/top-headlines?country=us&apiKey=ad73f35f96c244529436254ebe21c34c",!0),n.onload=function(){if(200===this.status){let n=JSON.parse(this.responseText).articles,t="";n.forEach((function(e){let n=`\n                <div class="single-news">\n                    <h4>${e.title}</h4>\n                    <img src="${e.urlToImage}" alt="${e.title}">\n                    <p>${e.description}</p>\n                    <a href="${e.url}">More</a>\n                </div>\n            `;t+=n})),e.innerHTML=t}else console.log("error")},n.send()})();